INSERT INTO `board_comment` VALUES (1,'NORMAL','시간이 파운드케이크와 같다라... 정말 멋진 말이네요. 곰곰히 생각하게 되는군요',1,0,'2023-02-15 12:49:26','귀염둥이',6,2655808838),(2,'NORMAL','아 파운드케이크 먹고싶다',1,1,'2023-02-15 13:12:24','핑구',6,2666829744),(3,'NORMAL','꼭보세요 거북이도 이해하는 영화랍니다',3,0,'2023-02-15 13:15:32','핑구',8,2666829744),(4,'NORMAL','오 영화본 입장에서 진짜 알고가면 나을것같아요ㅋㅋㅋㅋㅋ',4,0,'2023-02-15 13:16:10','핑구',10,2666829744),(5,'NORMAL','영화 봤는데 진짜 공감. 수작임',5,0,'2023-02-15 13:30:46','못본척',6,2656139612),(6,'DELETED','좋은 글이네요!ㅋㅋㅋㅋ',6,0,'2023-02-15 13:46:48','못본척',10,2656139612),(7,'NORMAL','와 정말요?',4,1,'2023-02-15 13:46:44','못본척',10,2656139612),(8,'NORMAL','주인공 커엽..',8,0,'2023-02-15 15:09:26','귀염둥이',13,2655808838),(9,'NORMAL','ㅋㅋㅋㅋㅋㅋㅋㅋ ㅋㅋㅋㅋㅋㅋㅋ',9,0,'2023-02-15 17:29:56','동동이',13,2652473320),(10,'DELETED','ㅋㅋ',10,0,'2023-02-15 19:32:43','동동이',12,2652473320),(11,'NORMAL','헐 다시 한번 봐야겠어요!!',11,0,'2023-02-16 01:48:57','동동이',12,2652473320),(12,'NORMAL','진짜 테넷 음향까지 미친영화;; 갓벽하다',12,0,'2023-02-16 09:51:44','핑구',20,2666829744),(13,'NORMAL','필연적으로 앞으로 되돌아가야 한다는 말이 정말 공감되네요. ',13,0,'2023-02-16 10:04:05','핑구',22,2666829744);
